import React,{useState} from 'react'
import axios from 'axios'
import './Login.css'
import {apiUrl} from '../../config'

function Login() {

    const initialValues = {
        Username:'',
        Password: ''
    }

    let axiosConfig = {
        headers: {
            'Access-Control-Allow-Origin': '*'
        }
    };

    const [info, setInfo] = useState(initialValues);


    const handleSubmit = e =>{
        e.preventDefault()
        //console.log(info)
        const proxyUrl = apiUrl + "authenticate/login"

        axios.post(proxyUrl,info,axiosConfig)
            .then(res => {
                if(res.data.isLoggedIn && res.data.role[0] === 'user'){

                    notify("Login Successful",'success');
                    var data={
                        isLoggedIn:res.data.isLoggedIn,
                        token:res.data.token,
                        user:res.data
                    }
                    localStorage.setItem('userLoggedIn', res.data.isLoggedIn);
                    localStorage.setItem('user',JSON.stringify(data));
                    setHomeRedirect(true)
                    window.location.reload(false)


                }
                else{
                    if(res.data.isLocked){
                        setLoading(false)
                        notify("Account Locked, Please try again after 15mins",'error');
                    }else{
                        setLoading(false)
                        notify("Login details incorrect",'error');
                    }

                }

            })
            .catch(e => {
                console.log(e)
                setLoading(false)
                notify("Login details incorrect",'error');
            })

    }

    return (
        <div className="home__hero-section">
            <div className='rowwer home__hero-row'>
                <div className="column gradcolor">
                    <div className='home__hero-text-wrapper'>
                        <img src="assets/images/womenInTech.png" alt='TechWomen' className="home__hero-design" />
                        <div className='top-line'> Bring home the magic of camp with Mission-T Summer Training Program </div>
                        <p className="home__hero-subtitle">The IHS Summer Training Program implemented by TechQuest STEM Academy is intended for students and Teachers. </p>
                    </div>
                </div>

                <div className="column signin_column">
                    <h4>Sign In</h4>
                    <h5>Welcome, Admin</h5>
                    <p> You can now login into your dashboard with your email and password.</p>

                    <div>
                        <form className="form-horizontal">
                            <div className="form-group">   
                                <input type="text" className="form-control" id="email" name="Email" placeholder="Email"/>
                            </div>
                            <div className="form-group">   
                                <input type="password" className="form-control" id="password" name="Password" placeholder="Password"/>
                            </div>
                            <div className="mt-3">
                                <button style={{color:'#fff'}} className="btn btn-pri btn-block waves-effect waves-light" type="submit">Sign In</button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Login
